mapper.select:

SELECT   o.* ,g.`goods_name`  FROM  t_order  AS o , t_goods AS g   , t_store AS s  WHERE  order_state =#{order_state}  

AND  s.`user_id` = #{user_id}
AND  o.`goods_id` = g.`goods_id`
AND  g.`store_id` = s.`store_id`
ORDER BY order_time DESC


	SELECT  longbi_pay_state , dianzibi_pay_state , yinlian_pay_state, pay_type
       FROM t_order    WHERE order_id=#{order_id}   
       ORDER BY order_time DESC
