mapper.select:
SELECT * FROM t_store 
	WHERE user_id=#{user_id} 
		order by store_state,store_create_time desc

mapper.sql:
FROM t_store AS ts JOIN t_street AS tst ON ts.street_id=tst.street_id JOIN t_region AS tr ON tr.region_id=tst.region_id JOIN t_city AS tc ON tc.city_id=tr.city_id WHERE ts.store_state=3
AND tc.city_name=#{cityName}
AND tr.region_id=#{region_id}
AND ts.street_id=#{street_id}
AND store_type2_id in (SELECT store_type2_id FROM t_store_type2 WHERE store_type1_id=#{store_type1_id})
AND store_type2_id = #{store_type2_id}

mapper.select:
SELECT
ts.*,tc.city_name,tr.region_name,tst.street_id


	LIMIT #{pageSize} OFFSET #{offset}
